import React, { useEffect, useState } from 'react';
import api from '../api';

function RecruiterDashboard({ user }) {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');
  const [description, setDescription] = useState('');

  const fetchJobs = async () => {
    try { const res = await api.get('/jobs/all'); setJobs(res.data.filter(j => j.postedBy?.username === user.username)); }
    catch (e) { console.error(e); }
  };

  useEffect(() => { fetchJobs(); }, []);

  const submit = async (e) => {
    e.preventDefault();
    await api.post('/jobs/create', { title, description, location, skills });
    setTitle(''); setLocation(''); setSkills(''); setDescription('');
    fetchJobs();
  };

  return (
    <div>
      <h3>Recruiter Dashboard</h3>

      <div className="card mb-3 p-3">
        <h5>Post a Job</h5>
        <form onSubmit={submit}>
          <div className="mb-2"><input className="form-control" placeholder="Title" value={title} onChange={e=>setTitle(e.target.value)} /></div>
          <div className="mb-2"><input className="form-control" placeholder="Location" value={location} onChange={e=>setLocation(e.target.value)} /></div>
          <div className="mb-2"><input className="form-control" placeholder="Skills (comma separated)" value={skills} onChange={e=>setSkills(e.target.value)} /></div>
          <div className="mb-2"><textarea className="form-control" placeholder="Description" value={description} onChange={e=>setDescription(e.target.value)} /></div>
          <button className="btn btn-primary">Post</button>
        </form>
      </div>

      <h5>Your Jobs</h5>
      <div className="row">
        {jobs.map(j => (
          <div key={j.id} className="col-md-6">
            <div className="card mb-2">
              <div className="card-body">
                <h5>{j.title}</h5>
                <p>{j.description}</p>
                <p><small>{j.location} â€” {j.skills}</small></p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecruiterDashboard;
