import React, { useState } from 'react';
import api from '../api/api';
import '../styles.css';

export default function PostJob() {
  const [form, setForm] = useState({ title: '', description: '', location: '', skills: '' });
  const [msg, setMsg] = useState('');

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/jobs/create', form);
      setMsg(res.data || 'Posted');
      setForm({ title: '', description: '', location: '', skills: '' });
    } catch (err) {
      setMsg('Failed to post job');
    }
  };

  return (
    <div className="card">
      <h2>Post Job</h2>
      {msg && <div className="info">{msg}</div>}
      <form onSubmit={submit} className="form">
        <input name="title" placeholder="Title" value={form.title} onChange={onChange} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={onChange} required />
        <input name="location" placeholder="Location" value={form.location} onChange={onChange} required />
        <input name="skills" placeholder="Skills (comma separated)" value={form.skills} onChange={onChange} />
        <button className="btn" type="submit">Post</button>
      </form>
    </div>
  );
}
