import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function AdminDashboard() {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({
    title: '',
    description: '',
    location: '',
    skills: ''
  });

  const load = async () => {
    try {
      const res = await api.get('/jobs/all');
      setJobs(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to load jobs');
    }
  };

  useEffect(() => { load(); }, []);

  const remove = async (id) => {
    if (!window.confirm('Delete job?')) return;
    try {
      await api.delete(`/jobs/delete/${id}`);
      load();
    } catch (err) {
      console.error(err);
      alert('Delete failed');
    }
  };

  const addJob = async () => {
    try {
      await api.post('/jobs/create', newJob);
      setNewJob({ title: '', description: '', location: '', skills: '' });
      load();
    } catch (err) {
      console.error(err);
      alert('Add job failed');
    }
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>

      {/* Add Job Form */}
      <div style={{ marginBottom: 20 }}>
        <h3>Add Job</h3>
        <input
          placeholder="Title"
          value={newJob.title}
          onChange={e => setNewJob({ ...newJob, title: e.target.value })}
        />
        <input
          placeholder="Description"
          value={newJob.description}
          onChange={e => setNewJob({ ...newJob, description: e.target.value })}
        />
        <input
          placeholder="Location"
          value={newJob.location}
          onChange={e => setNewJob({ ...newJob, location: e.target.value })}
        />
        <input
          placeholder="Skills"
          value={newJob.skills}
          onChange={e => setNewJob({ ...newJob, skills: e.target.value })}
        />
        <button onClick={addJob}>Post Job</button>
      </div>

      {/* Job List */}
      <div>
        {jobs.map(j => (
          <div key={j.id} style={{ marginBottom: 12, border: '1px solid #ddd', padding: 8 }}>
            <strong>{j.title}</strong>
            <div>{j.description}</div>
            <div>{j.location} â€” {j.skills}</div>
            <button onClick={() => remove(j.id)} style={{ marginTop:8 }}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}
