// src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

function normalizeRole(role) {
  if (!role) return '';
  return role.toUpperCase().replace(/^ROLE_/, '').replace(/[_-]/g, '');
}

function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [info, setInfo] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    if (params.get('sessionExpired') === 'true') {
      setInfo('Your session expired — please log in again.');
      params.delete('sessionExpired');
      window.history.replaceState({}, '', window.location.pathname);
    }
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    setError('');
    setInfo('');
    setLoading(true);

    try {
      const res = await api.post('/api/auth/login', { username, password });

      // If backend didn't return a user object, treat as error
      if (!res.data || !res.data.username) {
        throw new Error('Invalid login response');
      }

      const normalizedRole = normalizeRole(res.data.role);
      const userObj = {
        id: res.data.id,
        username: res.data.username,
        role: normalizedRole
      };

      setUser(userObj);

      // ✅ IMPORTANT: clear any "sessionExpired" from URL before navigation
      window.history.replaceState({}, '', window.location.pathname);

      if (normalizedRole.includes('ADMIN')) navigate('/admin');
      else if (normalizedRole.includes('RECRUITER')) navigate('/recruiter');
      else navigate('/jobseeker');
    } catch (err) {
      console.error(err);
      const msg = err.response?.data || 'Login failed';
      setError(typeof msg === 'string' ? msg : JSON.stringify(msg));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <h3>Login</h3>
        {info && <div className="alert alert-warning">{info}</div>}
        {error && <div className="alert alert-danger">{error}</div>}

        <form onSubmit={submit}>
          <div className="mb-3">
            <label>Username</label>
            <input
              className="form-control"
              value={username}
              onChange={e => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label>Password</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
          </div>
          <button className="btn btn-primary" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
