import React, { useState } from 'react';
import api from '../api/axiosConfig';

export default function CreateJob({ onCreated }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');

  async function submit(e){
    e.preventDefault();
    try{
      await api.post('/jobs/create', { title, description, location, skills });
      setTitle(''); setDescription(''); setLocation(''); setSkills('');
      if (onCreated) onCreated();
      alert('Job created');
    }catch(err){alert('Create failed')}
  }

  return (
    <div className="card" style={{marginTop:12}}>
      <h3>Create Job</h3>
      <form onSubmit={submit}>
        <label>Title</label>
        <input className="input" value={title} onChange={e=>setTitle(e.target.value)} />
        <label>Location</label>
        <input className="input" value={location} onChange={e=>setLocation(e.target.value)} />
        <label>Skills (comma separated)</label>
        <input className="input" value={skills} onChange={e=>setSkills(e.target.value)} />
        <label>Description</label>
        <textarea className="input" value={description} onChange={e=>setDescription(e.target.value)} rows={4} />
        <div style={{marginTop:8}}>
          <button className="btn" type="submit">Post Job</button>
        </div>
      </form>
    </div>
  );
}
