import React, { useEffect, useState } from 'react';
import api from '../api';

function Profile({ user }) {
  const [profile, setProfile] = useState(null);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [education, setEducation] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const res = await api.get('/profile/me');
        if (res.data) {
          setProfile(res.data);
          setFullName(res.data.fullName || '');
          setEmail(res.data.email || '');
          setMobileNo(res.data.mobileNo || '');
          setEducation(res.data.education || '');
        }
      } catch (e) {
        // no profile yet
      }
    })();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    await api.post('/profile/save', { fullName, email, mobileNo, education });
    alert('Profile saved');
  };

  return (
    <div className="col-md-8">
      <h3>Your Profile</h3>
      <form onSubmit={submit}>
        <div className="mb-2"><input className="form-control" placeholder="Full name" value={fullName} onChange={e=>setFullName(e.target.value)} /></div>
        <div className="mb-2"><input className="form-control" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} /></div>
        <div className="mb-2"><input className="form-control" placeholder="Mobile" value={mobileNo} onChange={e=>setMobileNo(e.target.value)} /></div>
        <div className="mb-2"><input className="form-control" placeholder="Education" value={education} onChange={e=>setEducation(e.target.value)} /></div>
        <button className="btn btn-primary">Save</button>
      </form>
    </div>
  );
}

export default Profile;
