// src/pages/RecruiterDashboard.js
import React, { useState, useEffect } from 'react';

function RecruiterDashboard() {
  const [jobs, setJobs] = useState([]);
  const [jobTitle, setJobTitle] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  // Fetch jobs posted by recruiter
  const fetchJobs = async () => {
    try {
      const res = await fetch('http://localhost:8080/api/jobs/recruiter', {
        credentials: 'include'
      });
      if (res.ok) {
        const data = await res.json();
        setJobs(data);
      }
    } catch (error) {
      console.error('Error fetching jobs', error);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  // Handle job post
  const handlePostJob = async (e) => {
    e.preventDefault();

    const jobData = { jobTitle, location, skills, description };

    try {
      const res = await fetch('http://localhost:8080/api/jobs', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(jobData)
      });

      if (res.ok) {
        setMessage('Job posted successfully!');
        setJobTitle('');
        setLocation('');
        setSkills('');
        setDescription('');
        fetchJobs();
      } else {
        const errorText = await res.text();
        setMessage(`Error posting job: ${errorText}`);
      }
    } catch (error) {
      console.error('Error posting job', error);
      setMessage('Error posting job');
    }
  };

  return (
    <div className="container">
      <h2>Recruiter Dashboard</h2>

      <form onSubmit={handlePostJob} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Job Title"
          value={jobTitle}
          onChange={(e) => setJobTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Skills (comma-separated)"
          value={skills}
          onChange={(e) => setSkills(e.target.value)}
          required
        />
        <textarea
          placeholder="Job Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Post Job</button>
      </form>

      {message && <p>{message}</p>}

      <h3>My Posted Jobs</h3>
      <ul>
        {jobs.map((job) => (
          <li key={job.id}>
            <strong>{job.jobTitle}</strong> - {job.location} <br />
            Skills: {job.skills} <br />
            {job.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default RecruiterDashboard;
