import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

function normalizeRole(role) {
  if (!role) return '';
  return role.toUpperCase().replace(/^ROLE_/, '').replace(/[_-]/g, '');
}

function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/api/auth/login', { username, password });
      const data = res.data;
      // Backend returns role normalized already, but handle either case:
      const normalizedRole = normalizeRole(data.role);
      const userObj = { id: data.id, username: data.username, role: normalizedRole };
      setUser(userObj);

      if (normalizedRole.includes('ADMIN')) navigate('/admin');
      else if (normalizedRole.includes('RECRUITER')) navigate('/recruiter');
      else navigate('/jobseeker');
    } catch (err) {
      setError(err.response?.data || 'Login failed');
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <h3>Login</h3>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={submit}>
          <div className="mb-3">
            <label>Username</label>
            <input className="form-control" value={username} onChange={e => setUsername(e.target.value)} />
          </div>
          <div className="mb-3">
            <label>Password</label>
            <input type="password" className="form-control" value={password} onChange={e => setPassword(e.target.value)} />
          </div>
          <button className="btn btn-primary">Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
