import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/axiosConfig';
import './Login.css';
import { ReactComponent as Logo } from '../assets/logo.svg'; // Optional logo

export default function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  async function submit(e) {
    e.preventDefault();
    setError('');
    try {
      const res = await api.post('/api/auth/login', { username, password }, { withCredentials: true });
      setUser(res.data);
      if (res.data.role === 'ADMIN') navigate('/admin');
      else if (res.data.role === 'RECRUITER') navigate('/recruiter');
      else navigate('/');
    } catch (err) {
      setError(err.response?.data || 'Login failed');
    }
  }

  return (
    <div className="d-flex flex-column min-vh-100">
      {/* Header */}
      <header className="app-header">
        <nav className="navbar navbar-expand-lg container">
          <div className="container-fluid">
            <a className="navbar-brand" href="/">
              <Logo /> {/* Optional logo */}
              YourApp
            </a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <a className="nav-link active" href="/">Home</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/about">About</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/contact">Contact</a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <div className="login-container container">
          <div className="login-hero">
            <div className="hero-text">
              <h1>Welcome Back</h1>
              <p>Log in to access your dashboard, manage your account, and more. If you don't have an account yet, contact your administrator.</p>
              <div className="d-flex gap-3">
                <button className="btn btn-outline-primary">Learn More</button>
                <button className="btn btn-outline-secondary">Contact Support</button>
              </div>
            </div>

            <div className="login-card">
              <h2 className="login-title">Sign In</h2>
              <form onSubmit={submit} className="login-form">
                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input 
                    id="username"
                    className="form-control" 
                    value={username} 
                    onChange={e => setUsername(e.target.value)} 
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input 
                    id="password"
                    type="password" 
                    className="form-control" 
                    value={password} 
                    onChange={e => setPassword(e.target.value)} 
                    required
                  />
                  <a href="/forgot-password" className="forgot-password">Forgot password?</a>
                </div>

                {error && <div className="error-message">{error}</div>}

                <button type="submit" className="login-btn">Login</button>
              </form>
            </div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="app-footer">
        <div className="container">
          <div className="footer-links">
            <a href="/privacy">Privacy Policy</a>
            <a href="/terms">Terms of Service</a>
            <a href="/contact">Contact Us</a>
          </div>
          <p className="copyright">Â© {new Date().getFullYear()} YourApp. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}
