import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';

const styles = {
  container: {
    maxWidth: 900,
    margin: '20px auto',
    padding: 16,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: '#f9fafb',
    borderRadius: 8,
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  card: {
    background: 'white',
    borderRadius: 8,
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
    padding: 20,
    marginBottom: 20,
  },
  userRow: {
    marginBottom: 8,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '8px 12px',
    border: '1.5px solid #ddd',
    borderRadius: 6,
    backgroundColor: '#fefefe',
  },
  btn: {
    backgroundColor: '#007bff',
    border: 'none',
    borderRadius: 5,
    padding: '6px 12px',
    color: 'white',
    fontWeight: 600,
    cursor: 'pointer',
    transition: 'background-color 0.25s ease',
    userSelect: 'none',
  },
  btnHover: {
    backgroundColor: '#0056b3',
  },
  applicants: {
    marginLeft: 12,
    marginBottom: 10,
    fontSize: '0.9em',
    color: '#555',
  },
  h2: {
    marginBottom: 16,
    color: '#222',
  },
  h3: {
    marginTop: 16,
    marginBottom: 12,
    color: '#444',
  },
};

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);

  async function fetchUsers() {
    try {
      const res = await api.get('/api/admin/users');
      setUsers(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchJobs() {
    try {
      const res = await api.get('/api/admin/jobs');
      setJobs(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  useEffect(() => {
    fetchUsers();
    fetchJobs();
  }, []);

  async function deleteUser(id) {
    if (!window.confirm('Delete user?')) return;
    await api.delete('/api/admin/users/' + id);
    setUsers(users.filter(u => u.id !== id));
  }

  async function deleteJob(id) {
    if (!window.confirm('Delete job?')) return;
    await api.delete('/api/admin/jobs/' + id);
    setJobs(jobs.filter(j => j.id !== id));
  }

  function getApplicantNames(applicantIds) {
    if (!applicantIds || applicantIds.length === 0) return 'No applicants';
    const applicantUsers = users.filter(u => applicantIds.includes(u.id));
    return applicantUsers.map(u => u.username).join(', ');
  }

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.h2}>Admin Panel</h2>
        <h3 style={styles.h3}>Users</h3>
        {users.map(u => (
          <div key={u.id} style={styles.userRow}>
            <div>
              {u.username} â€¢ {u.role}
            </div>
            <button
              style={styles.btn}
              onClick={() => deleteUser(u.id)}
              onMouseOver={e => (e.currentTarget.style.backgroundColor = '#0056b3')}
              onMouseOut={e => (e.currentTarget.style.backgroundColor = '#007bff')}
            >
              Delete
            </button>
          </div>
        ))}

        <h3 style={styles.h3}>Jobs</h3>
        {jobs.map(j => (
          <div key={j.id}>
            <JobCard job={j} onDelete={deleteJob} showDelete={true} />
            <div style={styles.applicants}>
              <strong>Applicants:</strong> {getApplicantNames(j.applicantIds)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
