import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Login from './pages/Login';
import Register from './pages/Register';
import AdminDashboard from './pages/AdminDashboard';
import RecruiterDashboard from './pages/RecruiterDashboard';
import JobSeekerDashboard from './pages/JobSeekerDashboard';
import JobList from './pages/JobList';
import Profile from './pages/Profile';
import ResumeUpload from './pages/ResumeUpload';
import api from './api';
import ProtectedRoute from './components/ProtectedRoute';
import SessionExpiredModal from './components/SessionExpiredModal';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSessionExpired, setShowSessionExpired] = useState(false);

  useEffect(() => {
    const checkSession = async () => {
      try {
        const res = await api.get('/api/auth/current');
        setUser(res.data);
      } catch (error) {
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    checkSession();

    // Check URL for session expired parameter
    const params = new URLSearchParams(window.location.search);
    if (params.get('sessionExpired') === 'true') {
      setShowSessionExpired(true);
    }
  }, []);

  if (loading) {
    return <div className="container mt-5">Loading...</div>;
  }

  return (
    <BrowserRouter>
      <Navbar user={user} setUser={setUser} />
      <div className="container mt-4">
        <Routes>
          <Route path="/" element={<JobList user={user} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register setUser={setUser} />} />
          <Route path="/jobs" element={<JobList user={user} />} />

          <Route element={<ProtectedRoute user={user} requiredRole="ADMIN" />}>
            <Route path="/admin" element={<AdminDashboard />} />
          </Route>

          <Route element={<ProtectedRoute user={user} requiredRole="RECRUITER" />}>
            <Route path="/recruiter" element={<RecruiterDashboard />} />
          </Route>

          <Route element={<ProtectedRoute user={user} requiredRole="JOBSEEKER" />}>
            <Route path="/jobseeker" element={<JobSeekerDashboard />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/resume" element={<ResumeUpload />} />
          </Route>

          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>

      <SessionExpiredModal 
        show={showSessionExpired}
        onHide={() => setShowSessionExpired(false)}
      />
    </BrowserRouter>
  );
}

export default App;
