import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import api from './api';
import Login from './pages/Login';
import Admin from './pages/Admin';
import Recruiter from './pages/Recruiter';
import JobSeeker from './pages/JobSeeker';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/api/auth/user', { withCredentials: true })
      .then(res => {
        setUser(res.data);
        setLoading(false);
      })
      .catch(() => {
        setUser(null);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/admin" element={user?.role === 'ADMIN' ? <Admin /> : <Navigate to="/login?sessionExpired=true" />} />
        <Route path="/recruiter" element={user?.role === 'RECRUITER' ? <Recruiter /> : <Navigate to="/login?sessionExpired=true" />} />
        <Route path="/jobseeker" element={user?.role === 'JOBSEEKER' ? <JobSeeker /> : <Navigate to="/login?sessionExpired=true" />} />
        <Route path="/" element={<Navigate to={user ? `/${user.role.toLowerCase()}` : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
