import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import '../styles.css';

export default function Register() {
  const [form, setForm] = useState({ username: '', password: '', role: 'JOBSEEKER' });
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setErr('');
    try {
      await api.post('/api/auth/register', form);
      navigate('/login');
    } catch (error) {
      setErr(error.response?.data || 'Registration failed');
    }
  };

  return (
    <div className="card">
      <h2>Register</h2>
      {err && <div className="error">{err}</div>}
      <form onSubmit={onSubmit} className="form">
        <input name="username" placeholder="Username" value={form.username} onChange={onChange} required />
        <input type="password" name="password" placeholder="Password" value={form.password} onChange={onChange} required />
        <select name="role" value={form.role} onChange={onChange}>
          <option value="JOBSEEKER">Job Seeker</option>
          <option value="RECRUITER">Recruiter</option>
          <option value="ADMIN">Admin</option>
        </select>
        <button className="btn" type="submit">Register</button>
      </form>
    </div>
  );
}
