import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function AdminDashboard() {
  const [jobs, setJobs] = useState([]);

  const load = async () => {
    const res = await api.get('/api/jobs/all');
    setJobs(res.data);
  };

  useEffect(() => { load(); }, []);

  const remove = async (id) => {
    if (!window.confirm('Delete job?')) return;
    try {
      await api.delete(`/api/jobs/${id}`);
      load();
    } catch (err) {
      console.error(err);
      alert('Delete failed');
    }
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <div>
        {jobs.map(j => (
          <div key={j.id} style={{ marginBottom: 12, border: '1px solid #ddd', padding: 8 }}>
            <strong>{j.jobTitle || j.title}</strong>
            <div>{j.description}</div>
            <div>{j.location} â€” {j.skills}</div>
            <button onClick={() => remove(j.id)} style={{ marginTop:8 }}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}
