import React, { useState } from 'react';
import api from '../services/api';

export default function ResumeUpload() {
  const [file, setFile] = useState(null);
  const [name, setName] = useState('');
  const [graduation, setGraduation] = useState('');
  const [projectDetails, setProjectDetails] = useState('');

  const submit = async (e) => {
    e.preventDefault();
    if (!file) return alert('Choose a file');
    const fd = new FormData();
    fd.append('file', file);
    fd.append('name', name);
    fd.append('graduation', graduation);
    fd.append('projectDetails', projectDetails);

    try {
      await api.post('/api/resume/upload', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Uploaded');
      setFile(null);
      setName(''); setGraduation(''); setProjectDetails('');
    } catch (err) {
      console.error(err);
      alert('Upload failed');
    }
  };

  return (
    <div style={{ maxWidth:600 }}>
      <h2>Upload Resume</h2>
      <form onSubmit={submit}>
        <div><input placeholder="Full name" value={name} onChange={e=>setName(e.target.value)} required /></div>
        <div style={{ marginTop:8 }}><input placeholder="Graduation details" value={graduation} onChange={e=>setGraduation(e.target.value)} /></div>
        <div style={{ marginTop:8 }}><textarea placeholder="Project details" value={projectDetails} onChange={e=>setProjectDetails(e.target.value)} /></div>
        <div style={{ marginTop:8 }}><input type="file" accept=".pdf,.doc,.docx" onChange={e=>setFile(e.target.files[0])} /></div>
        <div style={{ marginTop:12 }}><button type="submit">Upload</button></div>
      </form>
    </div>
  );
}
