import React, { useEffect, useState } from 'react';
import api from '../api';

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);

  const fetchUsers = async () => {
    try {
      const res = await api.get('/api/admin/users');
      setUsers(res.data);
    } catch (e) { console.error(e); }
  };
  const fetchJobs = async () => {
    try {
      const res = await api.get('/api/admin/jobs');
      setJobs(res.data);
    } catch (e) { console.error(e); }
  };

  useEffect(() => { fetchUsers(); fetchJobs(); }, []);

  const deleteUser = async (id) => {
    if (!window.confirm('Delete user?')) return;
    await api.delete(`/api/admin/users/${id}`);
    fetchUsers();
  };

  const deleteJob = async (id) => {
    if (!window.confirm('Delete job?')) return;
    await api.delete(`/jobs/delete/${id}`);
    fetchJobs();
  };

  return (
    <div>
      <h3>Admin Dashboard</h3>
      <h5 className="mt-4">Users</h5>
      <table className="table">
        <thead><tr><th>ID</th><th>Username</th><th>Role</th><th>Action</th></tr></thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.username}</td>
              <td>{u.role}</td>
              <td><button className="btn btn-sm btn-danger" onClick={() => deleteUser(u.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h5 className="mt-4">Jobs</h5>
      <table className="table">
        <thead><tr><th>ID</th><th>Title</th><th>Posted By</th><th>Action</th></tr></thead>
        <tbody>
          {jobs.map(j => (
            <tr key={j.id}>
              <td>{j.id}</td>
              <td>{j.title}</td>
              <td>{j.postedBy?.username}</td>
              <td><button className="btn btn-sm btn-danger" onClick={() => deleteJob(j.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminDashboard;
