import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);

  // Form fields state
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [education, setEducation] = useState('');
  const [resumeFile, setResumeFile] = useState(null);

  // Fetch profile on mount
  useEffect(() => {
    async function fetchProfile() {
      try {
        const res = await api.get('/profile/me', { withCredentials: true });
        if (res.data) {
          setProfile(res.data);
          setFullName(res.data.fullName || '');
          setEmail(res.data.email || '');
          setMobileNo(res.data.mobileNo || '');
          setEducation(res.data.education || '');
        } else {
          setProfile(null);
        }
      } catch (err) {
        console.error('Error fetching profile:', err);
        setProfile(null);
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, []);

  // Handle form submit for create/update
  async function handleSubmit(e) {
    e.preventDefault();
    const formData = new FormData();
    formData.append('fullName', fullName);
    formData.append('email', email);
    formData.append('mobileNo', mobileNo);
    formData.append('education', education);
    if (resumeFile) {
      formData.append('resume', resumeFile);
    }

    try {
      const res = await api.post('/profile/save', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        withCredentials: true,
      });
      alert(res.data);
      setEditMode(false);
      // Refresh profile data
      const refreshed = await api.get('/profile/me', { withCredentials: true });
      setProfile(refreshed.data);
    } catch (err) {
      console.error('Error saving profile:', err);
      alert('Failed to save profile');
    }
  }

  if (loading) return <div>Loading...</div>;

  // Show form if no profile or edit mode enabled
  if (!profile || editMode) {
    return (
      <div>
        <h2>{profile ? 'Edit' : 'Create'} Profile</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Full Name:</label><br />
            <input type="text" value={fullName} onChange={e => setFullName(e.target.value)} required />
          </div>
          <div>
            <label>Email:</label><br />
            <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
          </div>
          <div>
            <label>Mobile No:</label><br />
            <input type="text" value={mobileNo} onChange={e => setMobileNo(e.target.value)} required />
          </div>
          <div>
            <label>Education:</label><br />
            <input type="text" value={education} onChange={e => setEducation(e.target.value)} required />
          </div>
          <div>
            <label>Resume (PDF/DOC):</label><br />
            <input type="file" accept=".pdf,.doc,.docx" onChange={e => setResumeFile(e.target.files[0])} />
            {profile && profile.fileName && !resumeFile && (
              <p>Current file: {profile.fileName}</p>
            )}
          </div>
          <button type="submit">Save Profile</button>
          {profile && <button type="button" onClick={() => setEditMode(false)}>Cancel</button>}
        </form>
      </div>
    );
  }

  // Otherwise show profile details with edit button and resume link
  return (
    <div>
      <h2>My Profile</h2>
      <p><strong>Full Name:</strong> {profile.fullName}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Mobile No:</strong> {profile.mobileNo}</p>
      <p><strong>Education:</strong> {profile.education}</p>
      {profile.fileName && (
        <a href="/profile/resume" target="_blank" rel="noopener noreferrer">
          Download Resume: {profile.fileName}
        </a>
      )}
      <br />
      <button onClick={() => setEditMode(true)}>Edit Profile</button>
    </div>
  );
}
