import React, { useState } from 'react';
import axios from 'axios';

function CreateJob() {
  const [job, setJob] = useState({ title: '', description: '' });

  const handleChange = e => setJob({ ...job, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/jobs', job, { withCredentials: true });
      alert('Job created!');
    } catch {
      alert('Failed to create job.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Job</h2>
      <input name="title" placeholder="Job Title" onChange={handleChange} required />
      <textarea name="description" placeholder="Description" onChange={handleChange} required />
      <button type="submit">Create</button>
    </form>
  );
}

export default CreateJob;
