import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [filters, setFilters] = useState({ jobTitle: '', location: '', skills: '' });

  const load = async () => {
    try {
      if (filters.jobTitle || filters.location || filters.skills) {
        const params = {};
        if (filters.jobTitle) params.jobTitle = filters.jobTitle;
        if (filters.location) params.location = filters.location;
        if (filters.skills) params.skills = filters.skills;
        const res = await api.get('/api/jobs/filter', { params });
        setJobs(res.data);
      } else {
        const res = await api.get('/api/jobs/all');
        setJobs(res.data);
      }
    } catch (err) {
      console.error(err);
      alert('Failed to load jobs');
    }
  };

  useEffect(() => { load(); }, []); // initial

  const apply = async (jobId) => {
    // If you have an apply endpoint implement here.
    alert('Apply functionality should be implemented on backend. For now simulate apply.');
  };

  return (
    <div>
      <h2>Available Jobs</h2>

      <div style={{ marginBottom: 12 }}>
        <input placeholder="Job title" value={filters.jobTitle} onChange={e => setFilters({...filters, jobTitle: e.target.value})} />
        <input placeholder="Location" style={{ marginLeft:8 }} value={filters.location} onChange={e => setFilters({...filters, location: e.target.value})} />
        <input placeholder="Skills" style={{ marginLeft:8 }} value={filters.skills} onChange={e => setFilters({...filters, skills: e.target.value})} />
        <button style={{ marginLeft:8 }} onClick={load}>Search</button>
        <button style={{ marginLeft:8 }} onClick={() => { setFilters({ jobTitle:'', location:'', skills:'' }); load(); }}>Clear</button>
      </div>

      <ul>
        {jobs.map(j => (
          <li key={j.id} style={{ marginBottom:10 }}>
            <strong>{j.jobTitle || j.title || j.title}</strong><br />
            {j.description}<br />
            <small>{j.location} â€” Skills: {j.skills}</small><br />
            <button onClick={() => apply(j.id)} style={{ marginTop:6 }}>Apply</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
