import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // ⬅️ add loading state
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('/api/auth/me', { withCredentials: true })
      .then(res => {
        setUser(res.data);
        setLoading(false);
      })
      .catch(err => {
        setUser(null);
        setLoading(false);
        setError(err); // ⬅️ capture error
      });
  }, []);

  if (loading) return <div>Loading...</div>; // ✅ visible loading state
  if (error) return <div>Error loading app. Please try again.</div>; // ✅ fallback

  return (
    <AuthContext.Provider value={{ user, setUser }}>
      {children}
    </AuthContext.Provider>
  );
};
