import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';

export default function JobList({ user }) {
  const [jobs, setJobs] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [filter, setFilter] = useState({ title:'', location:'', skill:'' });

  async function fetchJobs() {
    try {
      const params = {};
      if (filter.title) params.title = filter.title;
      else if (filter.location) params.location = filter.location;
      else if (filter.skill) params.skill = filter.skill;
      const res = await api.get('/jobs/filter', { params });
      setJobs(res.data);
    } catch(err) {
      console.error(err);
    }
  }

  async function fetchAppliedJobs() {
    if (!user || user.role !== 'JOBSEEKER') return;
    try {
      const res = await api.get('/profile/me', { withCredentials: true });
      setAppliedJobs(res.data.appliedJobIds || []);
    } catch (err) {
      console.error(err);
    }
  }

  useEffect(() => { 
    fetchJobs(); 
    fetchAppliedJobs();
  }, []);

  async function handleDelete(id) {
    try {
      await api.delete('/jobs/delete/' + id);
      setJobs(jobs.filter(j => j.id !== id));
    } catch(err) {
      alert('Delete failed');
    }
  }

  async function handleApply(jobId) {
    try {
      await api.post(`/jobs/apply/${jobId}`, {}, { withCredentials: true });
      setAppliedJobs([...appliedJobs, jobId]);
      alert('Applied successfully!');
    } catch (err) {
      alert('Apply failed');
    }
  }

  return (
    <div className="container">
      <div className="card">
        <h2>Jobs</h2>
        <div className="row" style={{marginBottom:12}}>
          <input className="input" placeholder="Search title" value={filter.title} onChange={e=>setFilter({...filter,title:e.target.value})} />
          <input className="input" placeholder="Location" value={filter.location} onChange={e=>setFilter({...filter,location:e.target.value})} />
          <input className="input" placeholder="Skill" value={filter.skill} onChange={e=>setFilter({...filter,skill:e.target.value})} />
          <button className="btn" onClick={fetchJobs}>Search</button>
        </div>

        {jobs.length === 0 && <div className="small">No jobs found.</div>}
        {jobs.map(job => (
          <div key={job.id}>
            <JobCard
              job={job}
              onDelete={handleDelete}
              showDelete={
                user && (
                  user.role === 'ADMIN' ||
                  (user.role === 'RECRUITER' && job.postedBy && job.postedBy.username === user.username)
                )
              }
            />
            {user && user.role === 'JOBSEEKER' && (
              <button
                className="btn"
                style={{ marginTop: '8px' }}
                disabled={appliedJobs.includes(job.id)}
                onClick={() => handleApply(job.id)}
              >
                {appliedJobs.includes(job.id) ? 'Applied' : 'Apply'}
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
