import React, { useState } from 'react';
import axios from 'axios';

function JobPostForm() {
  const [job, setJob] = useState({ title: '', description: '' });

  const postJob = async () => {
    try {
      const res = await axios.post('http://localhost:8080/api/jobs', job, { withCredentials: true });
      alert(res.data);
    } catch {
      alert('Job post failed!');
    }
  };

  return (
    <div>
      <h2>Post a Job</h2>
      <input placeholder="Title" value={job.title} onChange={e => setJob({ ...job, title: e.target.value })} />
      <input placeholder="Description" value={job.description} onChange={e => setJob({ ...job, description: e.target.value })} />
      <button onClick={postJob}>Post Job</button>
    </div>
  );
}

export default JobPostForm;
