import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const normalizeRole = (role) =>
  (role || '').toUpperCase().replace(/^ROLE_/, '').replace(/[_-]/g, '');

function ProtectedRoute({ user, requiredRole, redirectPath = '/login' }) {
  if (!user) {
    return <Navigate to={redirectPath} replace />;
  }

  if (requiredRole) {
    const userRole = normalizeRole(user.role);
    const requiredRoleNormalized = normalizeRole(requiredRole);

    if (!userRole.includes(requiredRoleNormalized)) {
      return <Navigate to="/" replace />;
    }
  }

  return <Outlet />;
}

export default ProtectedRoute;
