import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import '../styles.css';

export default function Login({ setUser }) {
  const [form, setForm] = useState({ username: '', password: '' });
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setErr('');
    try {
      await api.post('/api/auth/login', form);
      const resp = await api.get('/api/auth/current');
      setUser(resp.data);
      // redirect to role dashboard
      const role = resp.data?.role?.toLowerCase();
      if (role) navigate(`/${role}`);
      else navigate('/');
    } catch (error) {
      setErr(error.response?.data || 'Login failed');
    }
  };

  return (
    <div className="card">
      <h2>Login</h2>
      {err && <div className="error">{err}</div>}
      <form onSubmit={onSubmit} className="form">
        <input name="username" placeholder="Username" value={form.username} onChange={onChange} required />
        <input type="password" name="password" placeholder="Password" value={form.password} onChange={onChange} required />
        <button className="btn" type="submit">Login</button>
      </form>
    </div>
  );
}
