import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../api/axiosConfig';

export default function Navbar({ user, setUser }) {
  const navigate = useNavigate();

  async function handleLogout() {
    try {
      await api.get('/api/auth/logout');
    } catch (e) {
      // ignore
    }
    setUser(null);
    navigate('/login');
  }

  return (
    <div className="nav container">
      <div>
        <Link to="/" style={{fontWeight:700, textDecoration:'none'}}>
          JobPortal
        </Link>
      </div>
      <div style={{display:'flex',gap:12,alignItems:'center'}}>
        <Link to="/jobs" className="small">Jobs</Link>
        {user && user.role === 'ADMIN' && <Link to="/admin" className="small">Admin</Link>}
        {user && user.role === 'RECRUITER' && <Link to="/recruiter" className="small">Recruiter</Link>}
        {user && user.role === 'JOBSEEKER' && <Link to="/me" className="small">Profile</Link>}

        {user ? (
          <>
            <span className="small">{user.username} ({user.role})</span>
            <button onClick={handleLogout} className="btn">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="btn">Login</Link>
            <Link to="/register" style={{marginLeft:8}} className="small">Register</Link>
          </>
        )}
      </div>
    </div>
  );
}
