import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';

export default function Profile({ user }){
  const [profile, setProfile] = useState({});
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [education, setEducation] = useState('');

  async function fetch(){
    try{ const res = await api.get('/profile/me'); setProfile(res.data || {}); }catch(e){console.log('no profile')} }

  useEffect(()=>{ fetch(); }, []);

  async function save(e){
    e.preventDefault();
    const payload = { fullName, email, mobileNo, education };
    try{ await api.post('/profile/save', payload); alert('Saved'); fetch(); }catch(e){alert('Save failed')}
  }

  return (
    <div className="container">
      <div className="card">
        <h2>My Profile</h2>
        <form onSubmit={save}>
          <label>Full name</label>
          <input className="input" value={fullName} onChange={e=>setFullName(e.target.value)} placeholder={profile.fullName || ''} />
          <label>Email</label>
          <input className="input" value={email} onChange={e=>setEmail(e.target.value)} placeholder={profile.email || ''} />
          <label>Mobile</label>
          <input className="input" value={mobileNo} onChange={e=>setMobileNo(e.target.value)} placeholder={profile.mobileNo || ''} />
          <label>Education</label>
          <input className="input" value={education} onChange={e=>setEducation(e.target.value)} placeholder={profile.education || ''} />
          <div style={{marginTop:8}}>
            <button className="btn" type="submit">Save Profile</button>
          </div>
        </form>
      </div>
    </div>
  );
}
