import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchProfile() {
      try {
        const res = await api.get('/profile/me', { withCredentials: true });
        setProfile(res.data);
      } catch (err) {
        console.error('Error fetching profile:', err);
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, []);

  if (loading) return <div>Loading profile...</div>;
  if (!profile) return <div>No profile found. Please create your profile.</div>;

  return (
    <div>
      <h2>My Profile</h2>
      <p><strong>Full Name:</strong> {profile.fullName}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Mobile No:</strong> {profile.mobileNo}</p>
      <p><strong>Education:</strong> {profile.education}</p>
      {profile.fileName && (
        <a href="/profile/resume" target="_blank" rel="noopener noreferrer">
          Download Resume: {profile.fileName}
        </a>
      )}
    </div>
  );
}
