import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../api/api';
import '../styles.css';

export default function Navbar({ user, setUser }) {
  const navigate = useNavigate();

  const logout = async () => {
    try {
      await api.get('/api/auth/logout');
    } catch (e) {
      // ignore errors
    }
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="nav">
      <div className="nav-left">
        <Link className="brand" to="/">JobPortal</Link>
      </div>
      <div className="nav-right">
        <Link to="/">Jobs</Link>
        {!user && <Link to="/login">Login</Link>}
        {!user && <Link to="/register">Register</Link>}

        {user && user.role === 'ADMIN' && <Link to="/admin">Admin</Link>}
        {user && user.role === 'RECRUITER' && <Link to="/recruiter">Recruiter</Link>}
        {user && user.role === 'JOBSEEKER' && <Link to="/jobseeker">JobSeeker</Link>}

        {user && <span className="nav-user">Hi, {user.username}</span>}
        {user && <button className="btn-link" onClick={logout}>Logout</button>}
      </div>
    </nav>
  );
}
