// src/pages/RecruiterDashboard.js
import React, { useState } from 'react';
import axios from '../api/axiosConfig';

export default function RecruiterDashboard() {
  const [job, setJob] = useState({
    title: '',
    description: '',
    location: '',
    skills: ''
  });

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/jobs/create', job, {
        withCredentials: true // VERY IMPORTANT for session auth
      });
      alert('Job created successfully!');
      console.log(res.data);
    } catch (err) {
      console.error('Error creating job:', err);
      alert('Failed to create job. Are you logged in as a recruiter?');
    }
  };

  return (
    <div>
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Job Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Job Description" onChange={handleChange} required />
        <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
        <input type="text" name="skills" placeholder="Skills (comma separated)" onChange={handleChange} required />
        <button type="submit">Create Job</button>
      </form>
    </div>
  );
}
