import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import api from './api';

// Pages
import Login from './pages/Login';
import AdminDashboard from './pages/AdminDashboard';
import RecruiterDashboard from './pages/RecruiterDashboard';
import JobSeekerDashboard from './pages/JobSeekerDashboard';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    api.get('/api/auth/me')
      .then(res => {
        if (res.data) setUser(res.data);
      })
      .catch(() => {
        setUser(null);
      });
  }, []);

  const ProtectedRoute = ({ children, role }) => {
    if (!user) return <Navigate to="/login?sessionExpired=true" replace />;
    if (role && user.role !== role) return <Navigate to="/" replace />;
    return children;
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/admin" element={<ProtectedRoute role="ADMIN"><AdminDashboard /></ProtectedRoute>} />
        <Route path="/recruiter" element={<ProtectedRoute role="RECRUITER"><RecruiterDashboard /></ProtectedRoute>} />
        <Route path="/jobseeker" element={<ProtectedRoute role="JOBSEEKER"><JobSeekerDashboard /></ProtectedRoute>} />
        <Route path="/" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
