import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import api from './api/axiosConfig';
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

import Login from './pages/Login';
import Register from './pages/Register';
import AdminDashboard from './pages/AdminDashboard';
import RecruiterDashboard from './pages/RecruiterDashboard';
import JobSeekerDashboard from './pages/JobSeekerDashboard';
import JobList from './pages/JobList';

function App(){
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  async function loadCurrent(){
    try{
      const res = await api.get('/api/auth/current');
      setUser(res.data);
    }catch(e){ setUser(null); }
  }

  useEffect(()=>{ loadCurrent(); }, []);

  return (
    <div>
      <Navbar user={user} setUser={setUser} />
      <Routes>
        <Route path="/" element={<JobList user={user} />} />
        <Route path="/jobs" element={<JobList user={user} />} />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register />} />

        <Route path="/admin" element={
          <ProtectedRoute user={user} roles={["ADMIN"]}>
            <AdminDashboard />
          </ProtectedRoute>
        } />

        <Route path="/recruiter" element={
          <ProtectedRoute user={user} roles={["RECRUITER"]}>
            <RecruiterDashboard user={user} />
          </ProtectedRoute>
        } />

        <Route path="/me" element={
          <ProtectedRoute user={user} roles={["JOBSEEKER"]}>
            <JobSeekerDashboard user={user} />
          </ProtectedRoute>
        } />

        <Route path="/jobs/all" element={<JobList user={user} />} />
      </Routes>
    </div>
  );
}

export default App;
