import React, { useState, useEffect } from 'react';
import api from '../api';

function ResumeUpload() {
  const [file, setFile] = useState(null);
  const [link, setLink] = useState('');

  const upload = async (e) => {
    e.preventDefault();
    if (!file) return alert('Choose a file');
    const form = new FormData();
    form.append('file', file);
    try {
      await api.post('/api/resume/upload', form, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Uploaded');
      view();
    } catch (err) {
      alert(err.response?.data || 'Upload failed');
    }
  };

  const view = async () => {
    try {
      const res = await api.get('/api/resume/view');
      if (res.data && res.data.startsWith('/resumes/')) setLink(`${process.env.REACT_APP_API_URL || 'http://localhost:8080'}${res.data}`);
      else if (res.data) alert(res.data);
    } catch (e) { console.error(e); }
  };

  useEffect(() => { view(); }, []);

  return (
    <div>
      <h3>Upload / View Resume</h3>
      <form onSubmit={upload} className="mb-3">
        <input type="file" onChange={e => setFile(e.target.files[0])} className="form-control mb-2" />
        <button className="btn btn-primary">Upload</button>
      </form>
      {link && (
        <div>
          <a href={link} target="_blank" rel="noreferrer">View uploaded resume</a>
        </div>
      )}
    </div>
  );
}

export default ResumeUpload;
