import React, { useState, useEffect } from 'react';
import api from '../api/axiosConfig';


export default function RecruiterDashboard({ user }) {
  const [job, setJob] = useState({
    title: '',
    description: '',
    location: '',
    skills: ''
  });
  const [myJobs, setMyJobs] = useState([]);
  const [applicantsByJob, setApplicantsByJob] = useState({}); // { jobId: [applicants] }

  useEffect(() => {
    if (user && user.role === 'RECRUITER') {
      fetchMyJobs();
    }
  }, [user]);

  const fetchMyJobs = async () => {
    try {
      const res = await api.get('/jobs/my-jobs', { withCredentials: true });
      setMyJobs(res.data);
      // For each job, fetch applicants
      for (const job of res.data) {
        fetchApplicants(job.id);
      }
    } catch (err) {
      console.error('Error fetching my jobs:', err);
    }
  };

  const fetchApplicants = async (jobId) => {
    try {
      const res = await api.get(`/jobs/applicants/${jobId}`, { withCredentials: true });
      setApplicantsByJob(prev => ({ ...prev, [jobId]: res.data }));
    } catch (err) {
      console.error(`Error fetching applicants for job ${jobId}:`, err);
    }
  };

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/jobs/create', job, { withCredentials: true });
      alert('Job created successfully!');
      setJob({ title: '', description: '', location: '', skills: '' });
      fetchMyJobs(); // refresh job list after create
    } catch (err) {
      console.error('Error creating job:', err);
      alert('Failed to create job. Are you logged in as a recruiter?');
    }
  };

  if (!user) {
    return <div>Please log in to access this page.</div>;
  }

  if (user.role !== 'RECRUITER') {
    return <div>Access denied. Only recruiters can create jobs and view applicants.</div>;
  }

  return (
    <div>
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Job Title"
          onChange={handleChange}
          value={job.title}
          required
        />
        <textarea
          name="description"
          placeholder="Job Description"
          onChange={handleChange}
          value={job.description}
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          onChange={handleChange}
          value={job.location}
          required
        />
        <input
          type="text"
          name="skills"
          placeholder="Skills (comma separated)"
          onChange={handleChange}
          value={job.skills}
          required
        />
        <button type="submit">Create Job</button>
      </form>

      <h2>My Posted Jobs</h2>
      {myJobs.length === 0 && <p>No jobs posted yet.</p>}
      {myJobs.map(job => (
        <div key={job.id} style={{ border: '1px solid #ccc', margin: '8px', padding: '8px' }}>
          <h3>{job.title}</h3>
          <p><b>Description:</b> {job.description}</p>
          <p><b>Location:</b> {job.location}</p>
          <p><b>Skills:</b> {job.skills}</p>

          <h4>Applicants:</h4>
          {applicantsByJob[job.id] && applicantsByJob[job.id].length > 0 ? (
            <ul>
              {applicantsByJob[job.id].map((applicant, index) => (
                <li key={index}>
                  <b>{applicant.fullName}</b> - {applicant.email} - {applicant.mobileNo} - {applicant.education}
                </li>
              ))}
            </ul>
          ) : (
            <p>No applicants yet.</p>
          )}
        </div>
      ))}
    </div>
  );
}
