import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);

  async function fetchUsers() {
    try {
      const res = await api.get('/api/admin/users');
      setUsers(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  async function fetchJobs() {
    try {
      const res = await api.get('/api/admin/jobs');
      setJobs(res.data);
    } catch (e) {
      console.error(e);
    }
  }

  useEffect(() => {
    fetchUsers();
    fetchJobs();
  }, []);

  async function deleteUser(id) {
    if (!window.confirm('Delete user?')) return;
    await api.delete('/api/admin/users/' + id);
    setUsers(users.filter(u => u.id !== id));
  }

  async function deleteJob(id) {
    if (!window.confirm('Delete job?')) return;
    await api.delete('/api/admin/jobs/' + id);
    setJobs(jobs.filter(j => j.id !== id));
  }

  // Add this helper function for applicants display
  function getApplicantNames(applicantIds) {
    if (!applicantIds || applicantIds.length === 0) return 'No applicants';
    const applicantUsers = users.filter(u => applicantIds.includes(u.id));
    return applicantUsers.map(u => u.username).join(', ');
  }

  return (
    <div className="container">
      <div className="card">
        <h2>Admin Panel</h2>
        <h3>Users</h3>
        {users.map(u => (
          <div
            key={u.id}
            className="card"
            style={{
              marginBottom: 8,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}
          >
            <div>
              {u.username} â€¢ {u.role}
            </div>
            <div>
              <button className="btn" onClick={() => deleteUser(u.id)}>
                Delete
              </button>
            </div>
          </div>
        ))}

        <h3 style={{ marginTop: 16 }}>Jobs</h3>
        {jobs.map(j => (
          <div key={j.id}>
            <JobCard job={j} onDelete={deleteJob} showDelete={true} />
            {/* New: Display applicant usernames below each job */}
            <div
              style={{
                marginLeft: 12,
                marginBottom: 10,
                fontSize: '0.9em',
                color: '#555',
              }}
            >
              <strong>Applicants:</strong> {getApplicantNames(j.applicantIds)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
