import React, { useState, useEffect } from "react";
import axios from "axios";

function JobList() {
  const [jobs, setJobs] = useState([]);
  const [filters, setFilters] = useState({ title: "", location: "", skill: "" });

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("/jobs/all");
      setJobs(res.data);
    } catch (error) {
      console.error("Error fetching jobs", error);
    }
  };

  const applyFilters = async () => {
    try {
      const queryParams = new URLSearchParams(filters).toString();
      const res = await axios.get(`/jobs/filter?${queryParams}`);
      setJobs(res.data);
    } catch (error) {
      console.error("Error filtering jobs", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Available Jobs</h2>

      {/* Filter Form */}
      <div style={{ marginBottom: "15px" }}>
        <input
          type="text"
          placeholder="Job Title"
          value={filters.title}
          onChange={(e) => setFilters({ ...filters, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={filters.location}
          onChange={(e) => setFilters({ ...filters, location: e.target.value })}
        />
        <input
          type="text"
          placeholder="Skill"
          value={filters.skill}
          onChange={(e) => setFilters({ ...filters, skill: e.target.value })}
        />
        <button onClick={applyFilters}>Apply Filters</button>
        <button onClick={fetchJobs}>Clear Filters</button>
      </div>

      {/* Jobs List */}
      <ul>
        {jobs.map((job) => (
          <li key={job.id} style={{ marginBottom: "10px" }}>
            <strong>{job.title}</strong> - {job.location} <br />
            Skills: {job.skills} <br />
            {job.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default JobList;
