import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

function ProtectedRoute({ user, requiredRole }) {
  if (!user) return <Navigate to="/login" replace />;
  // Roles in backend: ADMIN, RECRUITER, JOB_SEEKER (sometimes spelled JOBSEEKER). We'll accept JOBSEEKER and JOB_SEEKER.
  const normalized = (user.role || '').toUpperCase().replace('-', '').replace('_', '');
  const required = (requiredRole || '').toUpperCase().replace('-', '').replace('_', '');
  if (required && !normalized.includes(required)) {
    return <div className="alert alert-danger">Forbidden â€” you don't have access to this page.</div>;
  }
  return <Outlet />;
}

export default ProtectedRoute;
