import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';

export default function JobList({ user }) {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState({ title:'', location:'', skill:'' });

  async function fetchJobs() {
    try{
      const params = {};
      if (filter.title) params.title = filter.title;
      else if (filter.location) params.location = filter.location;
      else if (filter.skill) params.skill = filter.skill;
      const res = await api.get('/jobs/filter', { params });
      setJobs(res.data);
    }catch(err){
      console.error(err);
    }
  }

  useEffect(()=>{ fetchJobs(); }, []);

  async function handleDelete(id) {
    try{
      await api.delete('/jobs/delete/' + id);
      setJobs(jobs.filter(j=>j.id !== id));
    }catch(err){alert('Delete failed')}
  }

  return (
    <div className="container">
      <div className="card">
        <h2>Jobs</h2>
        <div className="row" style={{marginBottom:12}}>
          <input className="input" placeholder="Search title" value={filter.title} onChange={e=>setFilter({...filter,title:e.target.value})} />
          <input className="input" placeholder="Location" value={filter.location} onChange={e=>setFilter({...filter,location:e.target.value})} />
          <input className="input" placeholder="Skill" value={filter.skill} onChange={e=>setFilter({...filter,skill:e.target.value})} />
          <button className="btn" onClick={fetchJobs}>Search</button>
        </div>

        {jobs.length === 0 && <div className="small">No jobs found.</div>}
        {jobs.map(job => (
          <JobCard key={job.id} job={job} onDelete={handleDelete} showDelete={user && (user.role === 'ADMIN' || (user.role === 'RECRUITER' && job.postedBy && job.postedBy.username === user.username))} />
        ))}
      </div>
    </div>
  );
}
