import React, { useContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import JobList from './components/JobList';
import ResumeUpload from './components/ResumeUpload';
import JobPostForm from './components/JobPostForm';
import AdminDashboard from './components/AdminDashboard';

function App() {
  const { user } = useContext(AuthContext);

  return (
    <div>
      <h1>Job Portal System</h1>
      <Routes>
        <Route path="/" element={<JobList />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/resume-upload" element={user?.role === 'JOB_SEEKER' ? <ResumeUpload /> : <Navigate to="/login" />} />
        <Route path="/post-job" element={user?.role === 'RECRUITER' ? <JobPostForm /> : <Navigate to="/login" />} />
        <Route path="/admin-dashboard" element={user?.role === 'ADMIN' ? <AdminDashboard /> : <Navigate to="/login" />} />
      </Routes>
    </div>
  );
}

export default App;
