import React, { useState, useEffect } from "react";
import axios from "axios";

function AdminDashboard() {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({ title: "", location: "", skills: "", description: "" });

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("/jobs/all");
      setJobs(res.data);
    } catch (error) {
      console.error("Error fetching jobs", error);
    }
  };

  const postJob = async () => {
    try {
      await axios.post("/jobs/create", newJob);
      setNewJob({ title: "", location: "", skills: "", description: "" });
      fetchJobs();
    } catch (error) {
      console.error("Error posting job", error);
    }
  };

  const deleteJob = async (id) => {
    try {
      await axios.delete(`/jobs/delete/${id}`);
      fetchJobs();
    } catch (error) {
      console.error("Error deleting job", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Dashboard</h2>

      {/* Post New Job */}
      <div>
        <h3>Post New Job</h3>
        <input
          type="text"
          placeholder="Title"
          value={newJob.title}
          onChange={(e) => setNewJob({ ...newJob, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newJob.location}
          onChange={(e) => setNewJob({ ...newJob, location: e.target.value })}
        />
        <input
          type="text"
          placeholder="Skills"
          value={newJob.skills}
          onChange={(e) => setNewJob({ ...newJob, skills: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newJob.description}
          onChange={(e) => setNewJob({ ...newJob, description: e.target.value })}
        />
        <button onClick={postJob}>Post Job</button>
      </div>

      {/* Job List with Delete */}
      <h3>All Jobs</h3>
      <ul>
        {jobs.map((job) => (
          <li key={job.id} style={{ marginBottom: "10px" }}>
            <strong>{job.title}</strong> - {job.location} <br />
            Skills: {job.skills} <br />
            {job.description} <br />
            <button onClick={() => deleteJob(job.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
