import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';
import Profile from './Profile';

export default function JobSeekerDashboard({ user }) {
  const [jobs, setJobs] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [filter, setFilter] = useState({ title: '', location: '', skill: '' });
  const [loading, setLoading] = useState(false);
  const [showProfile, setShowProfile] = useState(true);
  const [selectedJob, setSelectedJob] = useState(null); // NEW: selected job for details view

  async function fetchJobs() {
    try {
      setLoading(true);
      const params = {};
      if (filter.title) params.title = filter.title;
      if (filter.location) params.location = filter.location;
      if (filter.skill) params.skill = filter.skill;

      const res = await api.get('/jobs/filter', { params });
      setJobs(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  async function fetchAppliedJobs() {
    try {
      const res = await api.get('/profile/me', { withCredentials: true });
      setAppliedJobs(res.data?.appliedJobIds || []);
    } catch (err) {
      console.error(err);
    }
  }

  useEffect(() => {
    fetchJobs();
    fetchAppliedJobs();
  }, []);

  async function handleApply(jobId) {
    try {
      setAppliedJobs((prev) => [...prev, jobId]);
      await api.post(`/jobs/apply/${jobId}`, {}, { withCredentials: true });
      alert('Applied successfully!');
    } catch (err) {
      alert('Apply failed');
      setAppliedJobs((prev) => prev.filter((id) => id !== jobId));
    }
  }

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      fetchJobs();
    }
  };

  // --- RENDER LOGIC ---

  if (selectedJob) {
    // Show single job details ONLY
    return (
      <div className="container">
        <button
          className="btn"
          onClick={() => setSelectedJob(null)}
          style={{ marginBottom: '1rem' }}
        >
          &larr; Back to Jobs
        </button>
        <JobCard job={selectedJob} showDelete={false} />
        <button
          className="btn"
          disabled={appliedJobs.includes(selectedJob.id)}
          onClick={() => handleApply(selectedJob.id)}
          style={{ marginTop: '6px' }}
        >
          {appliedJobs.includes(selectedJob.id) ? 'Applied' : 'Apply'}
        </button>
      </div>
    );
  }

  if (showProfile) {
    // Show profile ONLY
    return (
      <div className="container">
        <button
          className="btn"
          onClick={() => setShowProfile(false)}
          style={{ marginBottom: '1rem' }}
        >
          &larr; Back to Jobs
        </button>
        <Profile />
      </div>
    );
  }

  // Default: Show job list
  return (
    <div className="container">
      <button
        className="btn"
        onClick={() => setShowProfile(true)}
        style={{ marginBottom: '1rem' }}
      >
        View/Edit Profile
      </button>

      <h2>Available Jobs</h2>
      <div className="filters" style={{ marginBottom: 12 }}>
        <input
          className="input"
          placeholder="Search title"
          value={filter.title}
          onChange={(e) => setFilter({ ...filter, title: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <input
          className="input"
          placeholder="Location"
          value={filter.location}
          onChange={(e) => setFilter({ ...filter, location: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <input
          className="input"
          placeholder="Skill"
          value={filter.skill}
          onChange={(e) => setFilter({ ...filter, skill: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <button className="btn" onClick={fetchJobs} disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {loading && <div>Loading jobs...</div>}

      {!loading && jobs.length === 0 && <div>No jobs found.</div>}

      {jobs.map((job) => (
        <div key={job.id} style={{ marginBottom: '1rem' }}>
          {/* Pass a click handler to open job details */}
          <div
            style={{ cursor: 'pointer' }}
            onClick={() => setSelectedJob(job)}
          >
            <JobCard job={job} showDelete={false} />
          </div>
          <button
            className="btn"
            disabled={appliedJobs.includes(job.id)}
            onClick={() => handleApply(job.id)}
            style={{ marginTop: '6px' }}
          >
            {appliedJobs.includes(job.id) ? 'Applied' : 'Apply'}
          </button>
        </div>
      ))}
    </div>
  );
}
