import React, { useEffect, useState } from 'react';
import api from '../api/api';
import '../styles.css';

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [q, setQ] = useState('');

  const fetchAll = async () => {
    try {
      const res = await api.get('/jobs/all');
      setJobs(res.data || []);
    } catch (e) {
      console.error(e);
      setJobs([]);
    }
  };

  useEffect(() => {
    fetchAll();
  }, []);

  const search = async () => {
    try {
      const url = q ? `/jobs/filter?title=${encodeURIComponent(q)}` : '/jobs/all';
      const res = await api.get(url);
      setJobs(res.data || []);
    } catch (e) {
      console.error(e);
    }
  };

  return (
    <div className="page">
      <h2>All Jobs</h2>
      <div className="search-row">
        <input value={q} onChange={e => setQ(e.target.value)} placeholder="Search by title" />
        <button className="btn" onClick={search}>Search</button>
        <button className="btn" onClick={() => { setQ(''); fetchAll(); }}>Reset</button>
      </div>

      <div className="list">
        {jobs.length === 0 && <p>No jobs found.</p>}
        {jobs.map(job => (
          <div key={job.id} className="card-sm">
            <h3>{job.title}</h3>
            <p className="muted">{job.location} â€¢ <b>Skills:</b> {job.skills}</p>
            <p>{job.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
