import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';
import './Profile.css'; // for profile styles

export default function JobSeekerDashboard({ user }) {
  // Jobs related states
  const [jobs, setJobs] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [filter, setFilter] = useState({ title: '', location: '', skill: '' });
  const [loading, setLoading] = useState(false);

  // Profile related states
  const [profile, setProfile] = useState(null);
  const [profileLoading, setProfileLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [education, setEducation] = useState('');
  const [resumeFile, setResumeFile] = useState(null);

  // Fetch jobs with filter
  async function fetchJobs() {
    try {
      setLoading(true);
      const params = {};
      if (filter.title) params.title = filter.title;
      if (filter.location) params.location = filter.location;
      if (filter.skill) params.skill = filter.skill;

      const res = await api.get('/jobs/filter', { params });
      setJobs(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  // Fetch applied jobs IDs for this user
  async function fetchAppliedJobs() {
    try {
      const res = await api.get('/profile/me', { withCredentials: true });
      setAppliedJobs(res.data?.appliedJobIds || []);
    } catch (err) {
      console.error(err);
    }
  }

  // Fetch profile details
  async function fetchProfile() {
    try {
      setProfileLoading(true);
      const res = await api.get('/profile/me', { withCredentials: true });
      if (res.data) {
        setProfile(res.data);
        setFullName(res.data.fullName || '');
        setEmail(res.data.email || '');
        setMobileNo(res.data.mobileNo || '');
        setEducation(res.data.education || '');
      } else {
        setProfile(null);
      }
    } catch (err) {
      console.error('Error fetching profile:', err);
      setProfile(null);
    } finally {
      setProfileLoading(false);
    }
  }

  useEffect(() => {
    fetchJobs();
    fetchAppliedJobs();
    fetchProfile();
  }, []);

  // Apply for a job
  async function handleApply(jobId) {
    try {
      setAppliedJobs((prev) => [...prev, jobId]); // optimistic UI
      await api.post(`/jobs/apply/${jobId}`, {}, { withCredentials: true });
      alert('Applied successfully!');
    } catch (err) {
      alert('Apply failed');
      setAppliedJobs((prev) => prev.filter((id) => id !== jobId)); // rollback
    }
  }

  // Save profile changes
  async function handleSaveProfile(e) {
    e.preventDefault();
    const formData = new FormData();
    formData.append('fullName', fullName);
    formData.append('email', email);
    formData.append('mobileNo', mobileNo);
    formData.append('education', education);
    if (resumeFile) {
      formData.append('resume', resumeFile);
    }

    try {
      await api.post('/profile/save', formData, {
        withCredentials: true,
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setEditMode(false);
      setResumeFile(null);
      await fetchProfile();
      alert('Profile saved successfully!');
    } catch (err) {
      console.error('Error saving profile:', err);
      alert('Failed to save profile');
    }
  }

  // Download resume
  async function downloadResume() {
    try {
      const response = await fetch('/profile/resume', {
        credentials: 'include',
      });

      if (!response.ok) throw new Error('Failed to download resume');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;

      const disposition = response.headers.get('Content-Disposition');
      let filename = 'resume.pdf';
      if (disposition && disposition.includes('filename=')) {
        filename = disposition
          .split('filename=')[1]
          .split(';')[0]
          .replace(/"/g, '');
      }

      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      alert('Download failed: ' + error.message);
    }
  }

  // Handle Enter key in filter inputs
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      fetchJobs();
    }
  };

  return (
    <div className="container">
      {/* JOBS SECTION */}
      <h2>Available Jobs</h2>
      <div className="filters" style={{ marginBottom: 12 }}>
        <input
          className="input"
          placeholder="Search title"
          value={filter.title}
          onChange={(e) => setFilter({ ...filter, title: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <input
          className="input"
          placeholder="Location"
          value={filter.location}
          onChange={(e) => setFilter({ ...filter, location: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <input
          className="input"
          placeholder="Skill"
          value={filter.skill}
          onChange={(e) => setFilter({ ...filter, skill: e.target.value })}
          onKeyDown={handleKeyPress}
        />
        <button className="btn" onClick={fetchJobs} disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {loading && <div>Loading jobs...</div>}

      {!loading && jobs.length === 0 && <div>No jobs found.</div>}

      {jobs.map((job) => (
        <div key={job.id} style={{ marginBottom: '1rem' }}>
          <JobCard job={job} showDelete={false} />
          <button
            className="btn"
            disabled={appliedJobs.includes(job.id)}
            onClick={() => handleApply(job.id)}
            style={{ marginTop: '6px' }}
          >
            {appliedJobs.includes(job.id) ? 'Applied' : 'Apply'}
          </button>
        </div>
      ))}

      {/* PROFILE SECTION */}
      <hr style={{ margin: '2rem 0' }} />
      <h2>Your Profile</h2>

      {profileLoading ? (
        <div>Loading profile...</div>
      ) : !profile && !editMode ? (
        <div>
          <h3>No profile found.</h3>
          <button className="btn" onClick={() => setEditMode(true)}>
            Create Profile
          </button>
        </div>
      ) : editMode ? (
        <div className="profile-container">
          <div className="profile-card">
            <h3 className="profile-header">{profile ? 'Edit Profile' : 'Create Profile'}</h3>
            <form className="edit-form" onSubmit={handleSaveProfile}>
              <input
                name="fullName"
                className="form-input"
                placeholder="Full Name"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                required
              />
              <input
                name="email"
                type="email"
                className="form-input"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                name="mobileNo"
                className="form-input"
                placeholder="Mobile No"
                value={mobileNo}
                onChange={(e) => setMobileNo(e.target.value)}
                required
              />
              <input
                name="education"
                className="form-input"
                placeholder="Education"
                value={education}
                onChange={(e) => setEducation(e.target.value)}
                required
              />
              <input
                type="file"
                accept=".pdf,.doc,.docx"
                onChange={(e) => setResumeFile(e.target.files[0])}
              />
              <div className="form-actions">
                <button type="submit" className="edit-btn">
                  Save
                </button>
                <button type="button" className="cancel-btn" onClick={() => setEditMode(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      ) : (
        <div className="profile-container">
          <div className="profile-card">
            <div className="profile-info">
              <div className="info-item">
                <div className="info-label">Full Name:</div>
                <div className="info-value">{profile.fullName || 'N/A'}</div>
              </div>
              <div className="info-item">
                <div className="info-label">Email:</div>
                <div className="info-value">{profile.email || 'N/A'}</div>
              </div>
              <div className="info-item">
                <div className="info-label">Mobile No:</div>
                <div className="info-value">{profile.mobileNo || 'N/A'}</div>
              </div>
              <div className="info-item">
                <div className="info-label">Education:</div>
                <div className="info-value">{profile.education || 'N/A'}</div>
              </div>
            </div>

            <div className="resume-section">
              {profile.fileName ? (
                <button className="resume-btn" onClick={downloadResume}>
                  Download Resume ({profile.fileName})
                </button>
              ) : (
                <p>No resume uploaded</p>
              )}
              <button className="edit-btn" onClick={() => setEditMode(true)}>
                Edit Profile
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
