import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

function AdminDashboard() {
  const { user } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);

  const load = async () => {
    const u = await axios.get('/api/admin/users', { withCredentials: true });
    const j = await axios.get('/api/admin/jobs', { withCredentials: true });
    setUsers(u.data);
    setJobs(j.data);
  };

  useEffect(() => { if (user?.role === 'ADMIN') load(); }, [user]);

  const deleteUser = async id => {
    await axios.delete(`/api/admin/users/${id}`, { withCredentials: true });
    load();
  };
  const deleteJob = async id => {
    await axios.delete(`/api/admin/jobs/${id}`, { withCredentials: true });
    load();
  };

  if (user?.role !== 'ADMIN') return <p>Access denied</p>;
  return (
    <div>
      <h3>Admin Dashboard</h3>

      <h4>Users</h4>
      <table border="1">
        <thead><tr><th>ID</th><th>Username</th><th>Role</th><th>Action</th></tr></thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td><td>{u.username}</td><td>{u.role}</td>
              <td><button onClick={() => deleteUser(u.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h4>Jobs</h4>
      <table border="1">
        <thead><tr><th>ID</th><th>Title</th><th>Location</th><th>Action</th></tr></thead>
        <tbody>
          {jobs.map(j => (
            <tr key={j.id}>
              <td>{j.id}</td><td>{j.title}</td><td>{j.location}</td>
              <td><button onClick={() => deleteJob(j.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminDashboard;
