import React, { useState, useEffect } from "react";
import api from "../api/axiosConfig";

export default function RecruiterDashboard() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetchMyJobs();
  }, []);

  async function fetchMyJobs() {
    try {
      const res = await api.get("/jobs/my-jobs", { withCredentials: true });
      setJobs(res.data);
    } catch (err) {
      console.error(err);
    }
  }

  async function getApplicantName(userId) {
    try {
      const res = await api.get(`/profile/by-user/${userId}`, { withCredentials: true });
      return res.data.fullName || "Unknown";
    } catch {
      return "Unknown";
    }
  }

  return (
    <div>
      <h2>My Posted Jobs</h2>
      {jobs.map((job) => (
        <div key={job.id} className="card">
          <h3>{job.title}</h3>
          <p>{job.description}</p>
          <p>Applicants:</p>
          <ul>
            {job.applicantIds?.length ? (
              job.applicantIds.map((id) => <li key={id}>{id}</li>) // could replace with name lookup
            ) : (
              <li>No applicants yet</li>
            )}
          </ul>
        </div>
      ))}
    </div>
  );
}
