// src/pages/AdminDashboard.js
import React, { useEffect, useMemo, useState } from 'react';
import { FiUsers, FiBriefcase, FiTrendingUp, FiTrash2, FiBarChart2, FiSettings, FiHome } from 'react-icons/fi';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend, CartesianGrid,
} from 'recharts';
import api from '../api/axiosConfig';
import JobCard from '../components/JobCard';

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard'); // dashboard | users | jobs | reports
  const [loading, setLoading] = useState(false);
  const [searchUser, setSearchUser] = useState('');
  const [searchJob, setSearchJob] = useState('');
  const [confirming, setConfirming] = useState(null); // {type:'user'|'job', id:number} | null

  // ---- Fetchers ----
  const fetchUsers = async () => {
    try {
      const res = await api.get('/api/admin/users');
      setUsers(res.data || []);
    } catch (e) { console.error(e); }
  };
  const fetchJobs = async () => {
    try {
      const res = await api.get('/api/admin/jobs');
      setJobs(res.data || []);
    } catch (e) { console.error(e); }
  };

  useEffect(() => {
    setLoading(true);
    Promise.all([fetchUsers(), fetchJobs()]).finally(() => setLoading(false));
  }, []);

  // ---- Derived data ----
  const totalUsers = users.length;
  const totalJobs = jobs.length;
  const totalApplicants = useMemo(
    () => jobs.reduce((sum, j) => sum + (Array.isArray(j.applicantIds) ? j.applicantIds.length : 0), 0),
    [jobs]
  );

  const usersByRole = useMemo(() => {
    const acc = {};
    for (const u of users) {
      const role = (u.role || '').replace('ROLE_', '').toUpperCase();
      acc[role] = (acc[role] || 0) + 1;
    }
    return Object.entries(acc).map(([name, value]) => ({ name, value }));
  }, [users]);

  const jobsVsApplicants = useMemo(() => {
    return jobs.map(j => ({
      title: j.title?.length > 14 ? j.title.slice(0, 12) + 'â€¦' : j.title || 'Job',
      applicants: Array.isArray(j.applicantIds) ? j.applicantIds.length : 0,
    }));
  }, [jobs]);

  const filteredUsers = useMemo(() => {
    const q = searchUser.trim().toLowerCase();
    if (!q) return users;
    return users.filter(u =>
      (u.username || '').toLowerCase().includes(q) ||
      (u.role || '').toLowerCase().includes(q)
    );
  }, [users, searchUser]);

  const filteredJobs = useMemo(() => {
    const q = searchJob.trim().toLowerCase();
    if (!q) return jobs;
    return jobs.filter(j =>
      (j.title || '').toLowerCase().includes(q) ||
      (j.location || '').toLowerCase().includes(q) ||
      (j.skills || '').toLowerCase().includes(q)
    );
  }, [jobs, searchJob]);

  // ---- Mutations ----
  const deleteUser = async (id) => {
    try {
      await api.delete('/api/admin/users/' + id);
      setUsers(prev => prev.filter(u => u.id !== id));
    } catch (e) { console.error(e); }
  };
  const deleteJob = async (id) => {
    try {
      await api.delete('/api/admin/jobs/' + id);
      setJobs(prev => prev.filter(j => j.id !== id));
    } catch (e) { console.error(e); }
  };

  // ---- Styles (JS-only) ----
  const S = {
    app: { background: '#f5f7fb', minHeight: '100vh', color: '#202636', fontFamily: 'Inter, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif' },
    topbar: {
      height: 64, background: '#fff', boxShadow: '0 1px 0 rgba(16,24,40,.06)', display: 'flex',
      alignItems: 'center', justifyContent: 'space-between', padding: '0 20px', position: 'sticky', top: 0, zIndex: 10
    },
    brand: { display: 'flex', alignItems: 'center', gap: 10, fontWeight: 700, fontSize: 18 },
    layout: { display: 'grid', gridTemplateColumns: '260px 1fr', gap: 20, padding: 20 },
    sidebar: {
      background: '#0f172a', color: 'white', borderRadius: 16, padding: 14, height: 'calc(100vh - 104px)', position: 'sticky', top: 84,
      display: 'flex', flexDirection: 'column', gap: 6
    },
    sideHeader: { fontSize: 14, letterSpacing: .6, opacity: .7, margin: '6px 10px' },
    sideBtn: (active) => ({
      display: 'flex', alignItems: 'center', gap: 10, padding: '10px 12px', borderRadius: 10, cursor: 'pointer',
      background: active ? 'rgba(255,255,255,0.1)' : 'transparent', border: '1px solid rgba(255,255,255,.08)'
    }),
    main: { minWidth: 0 },
    section: { background: '#fff', borderRadius: 16, padding: 16, boxShadow: '0 1px 3px rgba(16,24,40,.08)' },
    grid3: { display: 'grid', gridTemplateColumns: 'repeat(3, minmax(0,1fr))', gap: 16 },
    statCard: { background: '#fff', borderRadius: 14, padding: 16, display: 'flex', alignItems: 'center', gap: 12, border: '1px solid #eef1f6' },
    statIcon: { width: 40, height: 40, display: 'grid', placeItems: 'center', borderRadius: 10, background: '#eef2ff' },
    h2: { fontSize: 18, fontWeight: 700, margin: '8px 0 12px' },
    list: { display: 'grid', gap: 12, marginTop: 12 },
    row: {
      display: 'grid', gridTemplateColumns: '1fr auto', alignItems: 'center',
      padding: 12, background: '#fff', borderRadius: 12, border: '1px solid #eef1f6'
    },
    input: {
      width: '100%', padding: '10px 12px', border: '1px solid #e5e7eb', borderRadius: 10, outline: 'none',
      background: '#fff'
    },
    rowActions: { display: 'flex', gap: 8 },
    btnDanger: {
      background: '#ef4444', color: '#fff', border: 0, borderRadius: 10, padding: '8px 10px', display: 'flex', alignItems: 'center', gap: 6, cursor: 'pointer'
    },
    headerRow: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 12, gap: 12, flexWrap: 'wrap' },
    pill: { padding: '6px 10px', background: '#f1f5f9', borderRadius: 999, fontSize: 12 },
    chartCard: { background: '#fff', borderRadius: 14, padding: 12, border: '1px solid #eef1f6' },
    chartsGrid: { display: 'grid', gridTemplateColumns: '1.2fr 1fr', gap: 16 },
    footerNote: { fontSize: 12, color: '#6b7280', marginTop: 8 }
  };

  const ROLE_COLORS = ['#60a5fa', '#34d399', '#f59e0b', '#a78bfa', '#f472b6', '#f87171'];

  // ---- Small helpers ----
  const SideItem = ({ icon, label, tab }) => (
    <div style={S.sideBtn(activeTab === tab)} onClick={() => setActiveTab(tab)}>
      {icon}
      <span style={{ fontWeight: 600 }}>{label}</span>
    </div>
  );

  return (
    <div style={S.app}>
      {/* Top Bar */}
      <div style={S.topbar}>
        <div style={S.brand}><FiBarChart2 /> JobPortal Admin</div>
        <div style={{ display: 'flex', alignItems: 'center', gap: 10 }}>
          <span style={S.pill}>Admin</span>
        </div>
      </div>

      {/* Layout */}
      <div style={S.layout}>
        {/* Sidebar */}
        <aside style={S.sidebar}>
          <div style={S.sideHeader}>ADMIN PANEL</div>
          <SideItem icon={<FiHome />} label="Dashboard" tab="dashboard" />
          <SideItem icon={<FiUsers />} label="Users" tab="users" />
          <SideItem icon={<FiBriefcase />} label="Jobs" tab="jobs" />
          <SideItem icon={<FiSettings />} label="Reports" tab="reports" />
          <div style={{ flex: 1 }} />
          <div style={{ ...S.sideHeader, opacity: .45 }}>Â© {new Date().getFullYear()}</div>
        </aside>

        {/* Main */}
        <main style={S.main}>
          {/* DASHBOARD */}
          {activeTab === 'dashboard' && (
            <div className="tab-dashboard" style={{ display: 'grid', gap: 16 }}>
              {/* Stats */}
              <section style={S.grid3}>
                <div style={S.statCard}>
                  <div style={S.statIcon}><FiUsers /></div>
                  <div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>Total Users</div>
                    <div style={{ fontSize: 22, fontWeight: 800 }}>{totalUsers}</div>
                  </div>
                </div>
                <div style={S.statCard}>
                  <div style={{ ...S.statIcon, background: '#ecfeff' }}><FiBriefcase /></div>
                  <div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>Total Jobs</div>
                    <div style={{ fontSize: 22, fontWeight: 800 }}>{totalJobs}</div>
                  </div>
                </div>
                <div style={S.statCard}>
                  <div style={{ ...S.statIcon, background: '#fef3c7' }}><FiTrendingUp /></div>
                  <div>
                    <div style={{ fontSize: 12, color: '#6b7280' }}>Applicants</div>
                    <div style={{ fontSize: 22, fontWeight: 800 }}>{totalApplicants}</div>
                  </div>
                </div>
              </section>

              {/* Charts */}
              <section style={S.chartsGrid}>
                <div style={S.chartCard}>
                  <div style={S.h2}>Jobs vs Applicants</div>
                  <div style={{ width: '100%', height: 280 }}>
                    <ResponsiveContainer>
                      <BarChart data={jobsVsApplicants} margin={{ top: 8, right: 8, left: 0, bottom: 8 }}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="title" />
                        <YAxis allowDecimals={false} />
                        <Tooltip />
                        <Bar dataKey="applicants" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                  <div style={S.footerNote}>Each bar shows the number of applicants per job.</div>
                </div>

                <div style={S.chartCard}>
                  <div style={S.h2}>Users by Role</div>
                  <div style={{ width: '100%', height: 280 }}>
                    <ResponsiveContainer>
                      <PieChart>
                        <Pie data={usersByRole} dataKey="value" nameKey="name" outerRadius={100} label>
                          {usersByRole.map((_, i) => (
                            <Cell key={i} fill={ROLE_COLORS[i % ROLE_COLORS.length]} />
                          ))}
                        </Pie>
                        <Legend />
                        <Tooltip />
                      </PieChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </section>
            </div>
          )}

          {/* USERS */}
          {activeTab === 'users' && (
            <section style={S.section}>
              <div style={S.headerRow}>
                <div style={S.h2}>Users</div>
                <input
                  style={{ ...S.input, maxWidth: 320 }}
                  placeholder="Search by username or roleâ€¦"
                  value={searchUser}
                  onChange={(e) => setSearchUser(e.target.value)}
                />
              </div>

              <div style={S.list}>
                {filteredUsers.map(u => (
                  <div key={u.id} style={S.row}>
                    <div>
                      <div style={{ fontWeight: 700 }}>{u.username}</div>
                      <div style={{ fontSize: 12, color: '#6b7280' }}>{(u.role || '').replace('ROLE_', '')}</div>
                    </div>
                    <div style={S.rowActions}>
                      <button
                        style={S.btnDanger}
                        onClick={() => setConfirming({ type: 'user', id: u.id })}
                        title="Delete user"
                      >
                        <FiTrash2 /> Delete
                      </button>
                    </div>
                  </div>
                ))}
                {filteredUsers.length === 0 && <div style={{ color: '#6b7280' }}>No users found.</div>}
              </div>
            </section>
          )}

          {/* JOBS */}
          {activeTab === 'jobs' && (
            <section style={S.section}>
              <div style={S.headerRow}>
                <div style={S.h2}>Jobs</div>
                <input
                  style={{ ...S.input, maxWidth: 320 }}
                  placeholder="Search by title, location, or skillâ€¦"
                  value={searchJob}
                  onChange={(e) => setSearchJob(e.target.value)}
                />
              </div>

              <div style={{ display: 'grid', gap: 12 }}>
                {filteredJobs.map(j => (
                  <div key={j.id} style={{ display: 'grid', gap: 6 }}>
                    <JobCard job={j} showDelete={false} />
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                      <div style={{ fontSize: 13, color: '#6b7280' }}>
                        Applicants: {Array.isArray(j.applicantIds) ? j.applicantIds.length : 0}
                      </div>
                      <button
                        style={S.btnDanger}
                        onClick={() => setConfirming({ type: 'job', id: j.id })}
                      >
                        <FiTrash2 /> Delete Job
                      </button>
                    </div>
                  </div>
                ))}
                {filteredJobs.length === 0 && <div style={{ color: '#6b7280' }}>No jobs found.</div>}
              </div>
            </section>
          )}

          {/* REPORTS (placeholder) */}
          {activeTab === 'reports' && (
            <section style={S.section}>
              <div style={S.h2}>Reports</div>
              <div style={{ color: '#6b7280' }}>
                Coming soon: export CSV, monthly summaries, and trend lines.
              </div>
            </section>
          )}

          {/* Loading overlay */}
          {loading && (
            <div style={{
              position: 'fixed', inset: 0, background: 'rgba(255,255,255,.7)',
              display: 'grid', placeItems: 'center', zIndex: 50, fontWeight: 700
            }}>
              Loadingâ€¦
            </div>
          )}

          {/* Confirm dialog (simple, JS-only) */}
          {confirming && (
            <div style={{
              position: 'fixed', inset: 0, background: 'rgba(15,23,42,.45)', display: 'grid', placeItems: 'center', zIndex: 60
            }}>
              <div style={{ background: '#fff', borderRadius: 14, padding: 18, width: 360, boxShadow: '0 10px 30px rgba(0,0,0,.15)' }}>
                <div style={{ fontWeight: 800, marginBottom: 8 }}>Confirm Delete</div>
                <div style={{ color: '#6b7280', marginBottom: 14 }}>
                  Are you sure you want to delete this {confirming.type}?
                </div>
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
                  <button
                    onClick={() => setConfirming(null)}
                    style={{ padding: '8px 10px', borderRadius: 10, border: '1px solid #e5e7eb', background: '#fff', cursor: 'pointer' }}
                  >
                    Cancel
                  </button>
                  <button
                    onClick={async () => {
                      const id = confirming.id;
                      const type = confirming.type;
                      setConfirming(null);
                      if (type === 'user') await deleteUser(id);
                      if (type === 'job') await deleteJob(id);
                    }}
                    style={{ ...S.btnDanger }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}
