import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function RecruiterDashboard() {
  const [resumes, setResumes] = useState([]);

  useEffect(() => {
    api.get('/api/resume/all')
      .then(res => setResumes(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2>Recruiter — Applicants</h2>
      {resumes.length === 0 ? <p>No resumes yet</p> : (
        <ul>
          {resumes.map(r => (
            <li key={r.id}>
              <strong>{r.name}</strong> — {r.graduation}
              <div>{r.projectDetails}</div>
              {r.filePath && <div><a href={r.filePath.startsWith('/') ? r.filePath : `/resumes/${r.filePath}`} target="_blank" rel="noreferrer">Download Resume</a></div>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
