import React, { useEffect, useState } from 'react';
import api from '../api/api';
import '../styles.css';

export default function Profile() {
  const [profile, setProfile] = useState({ fullName: '', email:'', mobileNo:'', education:'', resumeLink:'' });
  const [msg, setMsg] = useState('');

  useEffect(() => {
    const load = async () => {
      try {
        const res = await api.get('/profile/me');
        if (res.data) setProfile(res.data);
      } catch (e) {
        // no profile yet
      }
    };
    load();
  }, []);

  const onChange = (e) => setProfile({ ...profile, [e.target.name]: e.target.value });

  const save = async (e) => {
    e.preventDefault();
    try {
      await api.post('/profile/save', profile);
      setMsg('Profile saved.');
    } catch (e) {
      setMsg('Save failed');
    }
  };

  return (
    <div className="card">
      <h2>Profile</h2>
      {msg && <div className="info">{msg}</div>}
      <form className="form" onSubmit={save}>
        <input name="fullName" placeholder="Full name" value={profile.fullName || ''} onChange={onChange} />
        <input name="email" placeholder="Email" value={profile.email || ''} onChange={onChange} />
        <input name="mobileNo" placeholder="Mobile" value={profile.mobileNo || ''} onChange={onChange} />
        <input name="education" placeholder="Education" value={profile.education || ''} onChange={onChange} />
        <input name="resumeLink" placeholder="Resume link (optional)" value={profile.resumeLink || ''} onChange={onChange} />
        <button className="btn" type="submit">Save</button>
      </form>
    </div>
  );
}
