import React, { useEffect, useState } from 'react';
import api from '../api';

function RecruiterDashboard() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState('');
  const [location, setLocation] = useState('');
  const [skills, setSkills] = useState('');
  const [description, setDescription] = useState('');

  const fetchJobs = async () => {
    try {
      const res = await api.get('/jobs/my-jobs');
      setJobs(res.data);
    } catch (e) {
      console.error("Error:", e.response?.data || e.message);
      alert("Please login again");
      window.location.href = "/login";
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const submit = async (e) => {
    e.preventDefault();

    try {
      // Check session status before posting job
      await api.get('/api/auth/current');

      await api.post('/jobs/create', {
        title,
        description,
        location,
        skills
      });

      alert("Job posted successfully!");
      setTitle('');
      setLocation('');
      setSkills('');
      setDescription('');
      fetchJobs();

    } catch (err) {
      console.error("Full error:", err);
      if (err.response?.status === 401) {
        alert("SESSION EXPIRED - Please login again");
        window.location.href = "/login";
      } else {
        alert(err.response?.data || "Job posting failed");
      }
    }
  };

  return (
    <div className="container mt-4">
      <h3>Recruiter Dashboard</h3>

      <div className="card mb-4 p-3">
        <h5>Post New Job</h5>
        <form onSubmit={submit}>
          <input
            className="form-control mb-3"
            placeholder="Job Title*"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <input
            className="form-control mb-3"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <input
            className="form-control mb-3"
            placeholder="Skills (e.g., Java, React)"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
          />
          <textarea
            className="form-control mb-3"
            placeholder="Description*"
            rows={4}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <button type="submit" className="btn btn-primary">
            Post Job
          </button>
        </form>
      </div>

      <h5>Your Posted Jobs</h5>
      {jobs.length > 0 ? (
        <div className="row">
          {jobs.map((job) => (
            <div key={job.id} className="col-md-6 mb-3">
              <div className="card">
                <div className="card-body">
                  <h5>{job.title}</h5>
                  <p className="text-muted">{job.location}</p>
                  <p>{job.description}</p>
                  <p><small>Skills: {job.skills || "Not specified"}</small></p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No jobs posted yet.</p>
      )}
    </div>
  );
}

export default RecruiterDashboard;
