import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';
import { AuthContext } from '../context/AuthContext';

export default function Navbar() {
  const { user, setUser } = useContext(AuthContext);
  const navigate = useNavigate();

  const logout = async () => {
    try {
      await api.post('/api/auth/logout'); // or GET /api/auth/logout depending backend
    } catch (e) { /* ignore */ }
    setUser(null);
    sessionStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <nav style={{ padding: 10, borderBottom: '1px solid #ddd' }}>
      <Link to="/" style={{ marginRight: 12 }}>Home</Link>
      {!user && <Link to="/login" style={{ marginRight: 12 }}>Login</Link>}
      {!user && <Link to="/register" style={{ marginRight: 12 }}>Register</Link>}
      {user && user.role === 'JOB_SEEKER' && (
        <Link to="/jobseeker/upload" style={{ marginRight: 12 }}>Upload Resume</Link>
      )}
      {user && (user.role === 'RECRUITER' || user.role === 'ADMIN') && (
        <Link to="/post-job" style={{ marginRight: 12 }}>Post Job</Link>
      )}
      {user && user.role === 'RECRUITER' && (
        <Link to="/recruiter/dashboard" style={{ marginRight: 12 }}>Recruiter Dashboard</Link>
      )}
      {user && user.role === 'ADMIN' && (
        <Link to="/admin" style={{ marginRight: 12 }}>Admin Dashboard</Link>
      )}
      {user && (
        <span style={{ float: 'right' }}>
          <span style={{ marginRight: 8 }}>{user.username} ({user.role})</span>
          <button onClick={logout}>Logout</button>
        </span>
      )}
    </nav>
  );
}
