import React, { useEffect, useState } from 'react';
import api from '../api/api';
import '../styles.css';

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [err, setErr] = useState('');

  const fetch = async () => {
    try {
      const [uRes, jRes] = await Promise.all([
        api.get('/api/admin/users'),
        api.get('/jobs/all') // adminJobs endpoint not necessary; list all jobs
      ]);
      setUsers(uRes.data || []);
      setJobs(jRes.data || []);
    } catch (e) {
      setErr('Failed to load admin data');
    }
  };

  useEffect(() => { fetch(); }, []);

  const delUser = async (id) => {
    if (!window.confirm('Delete user?')) return;
    try {
      await api.delete(`/api/admin/users/${id}`);
      setUsers(users.filter(u => u.id !== id));
    } catch (e) {
      setErr('Failed to delete user');
    }
  };

  const delJob = async (id) => {
    if (!window.confirm('Delete job?')) return;
    try {
      await api.delete(`/api/admin/jobs/${id}`);
      setJobs(jobs.filter(j => j.id !== id));
    } catch (e) {
      setErr('Failed to delete job');
    }
  };

  return (
    <div className="page">
      <h2>Admin Dashboard</h2>
      {err && <div className="error">{err}</div>}

      <section>
        <h3>Users</h3>
        <div className="list">
          {users.map(u => (
            <div key={u.id} className="card-sm">
              <div>{u.username} <span className="muted">({u.role?.replace?.('ROLE_','') || u.role})</span></div>
              <div><button className="btn-danger" onClick={() => delUser(u.id)}>Delete</button></div>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h3>Jobs</h3>
        <div className="list">
          {jobs.map(j => (
            <div key={j.id} className="card-sm">
              <div><b>{j.title}</b> â€” {j.location}</div>
              <div>
                <button className="btn-danger" onClick={() => delJob(j.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
